import React from "react";
import { Form, Input, Button, Space } from "antd";
import { Link } from "react-router-dom";
import axios from "axios";
import toast from "react-hot-toast";

function Register() {
  // Called when form is successfully submitted
  const onFinish = async (values) => {
    try {
      // POST the form values to the registration endpoint
      const response = await axios.post("/api/user/register", values);

      // Handle success or error response from the server
      if (response.data.success) {
        toast.success(response.data.msg); // Show success message
      } else {
        toast.error(response.data.msg); // Show error message
      }
    } catch (error) {
      console.error("Registration error:", error);
      toast.error("Something went wrong during registration"); // Show general error message
    }
  };

  return (
    <div className="authentication">
      <div className="authentication-form card p-3">
        <h1 className="card-title">Nice to meet you</h1>
        <Form layout="vertical" onFinish={onFinish}>
          <Form.Item
            label="Name"
            name="name"
            rules={[{ required: true, message: "Please enter your name" }]}
          >
            <Input placeholder="Username" />
          </Form.Item>
          <Form.Item
            label="Email"
            name="email"
            rules={[{ required: true, type: "email", message: "Please enter a valid email" }]}
          >
            <Input placeholder="Email" />
          </Form.Item>
          <Form.Item
            label="Password"
            name="password"
            rules={[{ required: true, message: "Please enter a password" }]}
          >
            <Input type="password" placeholder="Password" />
          </Form.Item>
          <Form.Item
            label="Confirm Password"
            name="confirm_password"
            rules={[{ required: true, message: "Please confirm your password" }]}
          >
            <Input type="password" placeholder="Confirm Password" />
          </Form.Item>
          <Space direction="vertical" size="small">
            <Button className="primary-button my-3" htmlType="submit" type="primary">
              REGISTER
            </Button>
            <Link className="anchor" to="/login">
              Log In to Continue
            </Link>
          </Space>
        </Form>
      </div>
    </div>
  );
}

export default Register;
